<script>
  import _ from 'lodash';

  export default {
    name: 'MusclesSelector',
    props: ['selectedMuscles'],
    data() {
      return {
        view: 'back',
        map: null
      }
    },
    mounted() {

      this.mapFront = $(`.map-front`).mapify({
        hoverClass: "selected",
        instantClickOnMobile: true,
        onClick: (groupId) => {
        }
      })
        .data('plugin_mapify');

      //      this.mapBack = $(`.map-back`).mapify({
      //        hoverClass: "selected",
      //        instantClickOnMobile: true,
      //        onClick: (area) => {
      //          let region = this.getAreaRegion(area);
      //          this.switchSelectedRegion(region.name);
      //        }
      //      })
      //        .data('plugin_mapify');
    },
    methods: {
      switchSelectedRegion(regionName) {
        let region = _.find(this.regions, {name: regionName});
        this.setRegion(regionName, !region.selected);
      },

      setGroup(groupId, value) {
        let region = _.find(this.regions, {name: regionName});
        region.selected = value;
        if (value && (this.selectedMuscles.indexOf(regionName) < 0)) {
          this.selectedMuscles.push(regionName);
        } else if (!value && this.selectedMuscles.indexOf(regionName) > -1) {
          let index = this.selectedMuscles.indexOf(regionName);
          this.selectedMuscles.splice(index, 1);
        }
      },

      highlightSelectedAreas() {
        let newAreasToSet = {};
        this.regions.forEach((region) => {
          if (region.selected) {
            region.areas.forEach((area) => {
              newAreasToSet[area] = true;
            })
          } else {
            region.areas.forEach((area) => {
              newAreasToSet[area] = false;
            })
          }
        });
        this.mapFront.setSelectedAreas(newAreasToSet);
        this.mapBack.setSelectedAreas(newAreasToSet);
      },
    },
    computed: {
      updateSelected() {
        let selectedMuscles = this.selectedMuscles;
        console.log(selectedMuscles);
        this.$nextTick(() => {
          this.regions.forEach((region) => {
            if (this.selectedMuscles.indexOf(region.name) >= 0) {
              this.setRegion(region.name, true);
            } else {
              this.setRegion(region.name, false);
            }
          });
          setTimeout(() => {
            this.highlightSelectedAreas();
          })
        });
      }
    }
  }
</script>

