<script>
  import _ from 'lodash';
  import musclesStore from '../../store/muscles';

  export default {
    name: 'MusclesSelector',
    props: ['selectedMuscles'],
    data() {
      return {
        view: 'front',
        map: null
      }
    },
    mounted() {
      this.initMap();
    },
    methods: {
      rotate() {
        if (this.view === 'front') {
          this.view = 'back';
        } else {
          this.view = 'front';
        }

      },
      initMap() {
        let self = this;
        console.log('initing');
        $(`.map-front`).mapify({
          instantClickOnMobile: true,
          onClick(title, groupId) {
            self.switchGroup(groupId);
          }
        });
        $(`.map-back`).mapify({
          instantClickOnMobile: true,
          onClick(title, groupId) {
            self.switchGroup(groupId);
          }
        });

        this.updateMap();
      },
      switchGroup(groupId) {
        if (this.selectedMuscles.indexOf(groupId) === -1) {
          this.selectedMuscles.push(groupId);
        } else {
          let index = this.selectedMuscles.indexOf(groupId);
          if (index >= 0) {
            this.selectedMuscles.splice(index, 1);
          }
        }
      },
      labelMuscleRemoveClicked(mKey) {
        let index = this.selectedMuscles.indexOf(mKey);
        this.selectedMuscles.splice(index, 1);
      },
      updateMap() {
        let selectedMuscles = this.selectedMuscles;
        console.log('update');
        this.$nextTick(() => {
          let areas = $('area');

          areas.each((index, area) => {
            let $area = $(area);

            let areaGroupId = $area.attr('data-group-id');

            let $polygon = $(`polygon[data-group-id=${areaGroupId}]`);
            if (selectedMuscles.indexOf(areaGroupId) >= 0) {
              $polygon.addClass('selected');
            } else {
              $polygon.attr('class', 'mapify-polygon');
            }
          })
        })
      }
    },
    computed: {
      updateSelected() {
        let selectedMuscles = this.selectedMuscles;
        this.updateMap();
      },
      selectedMusclesFull() {
        return this.selectedMuscles.map((mKey) => musclesStore.getMuscle(mKey));
      }
    }
  }
</script>

